import React from 'react';
import './RingChart.css'; // Подключаем стили

const RingChart = ({ data }) => {
    const total = data.reduce((sum, segment) => sum + segment[1], 0);

    // Создаем градиентные остановки
    let cumulativePercentage = 0;
    const gradientStops = data.flatMap(([name, value, color]) => {
        const percentage = (value / total) * 100;
        const startPercentage = cumulativePercentage;
        const endPercentage = startPercentage + percentage;
        cumulativePercentage += percentage;

        return [
            { color, offset: `${startPercentage}%` },
            { color, offset: `${endPercentage}%` },
        ];
    });

    return (
        <div className="donut-chart">
            <div className="donut-container">
                <svg className="donut-svg" viewBox="0 0 36 36">
                    <defs>
                        <linearGradient id="gradient" gradientUnits="userSpaceOnUse" x1="0" y1="0" x2="36" y2="36">
                            {gradientStops.map((stop, index) => (
                                <stop
                                    key={index}
                                    offset={stop.offset}
                                    stopColor={stop.color}
                                />
                            ))}
                        </linearGradient>
                    </defs>
                    <circle
                        className="donut-background"
                        cx="18"
                        cy="18"
                        r="15.915"
                        fill="transparent"
                        stroke="#e6e6e6"
                        strokeWidth="3"
                    />
                    <circle
                        className="donut-progress"
                        cx="18"
                        cy="18"
                        r="15.915"
                        fill="transparent"
                        stroke="url(#gradient)"
                        strokeWidth="3"
                        strokeDasharray="100 100"
                        transform="rotate(-90 18 18)"
                    />
                </svg>
            </div>

            <div className="donut-labels">
                {data.map(([name, value, color, emoji], index) => {
                    const truncatedName =
                        name.length > 8 ? `${name.slice(0, 8)}...` : name;
                    const percentage = ((value / total) * 100).toFixed(1);

                    return (
                        <div key={index} className="donut-label" style={{ color }}>
                            <span>{emoji} {truncatedName} - {percentage}%</span>
                        </div>
                    );
                })}
            </div>
        </div>
    );
};

export default RingChart;
