import React from "react";

const RingChart = ({ data }) => {
  // Считаем общую сумму всех значений
  const totalValue = data.reduce((sum, segment) => sum + segment[1], 0);

  // Рассчитываем углы и цвета для каждого сегмента
  let currentAngle = 0;
  const segments = data.map(([name, value, color]) => {
    const percentage = (value / totalValue) * 100;
    const startAngle = currentAngle;
    const endAngle = currentAngle + (percentage / 100) * 360;
    currentAngle = endAngle;
    return { name, percentage, color, startAngle, endAngle };
  });

  const renderSegments = () => {
    return segments.map((segment, index) => {
      const largeArcFlag = segment.endAngle - segment.startAngle > 180 ? 1 : 0;
      const startRadians = (Math.PI * segment.startAngle) / 180;
      const endRadians = (Math.PI * segment.endAngle) / 180;

      const startX = 50 + 50 * Math.cos(startRadians);
      const startY = 50 + 50 * Math.sin(startRadians);
      const endX = 50 + 50 * Math.cos(endRadians);
      const endY = 50 + 50 * Math.sin(endRadians);

      const pathData = `M 50 50 L ${startX} ${startY} A 50 50 0 ${largeArcFlag} 1 ${endX} ${endY} Z`;

      return <path key={index} d={pathData} fill={segment.color} />;
    });
  };

  return (
    <div style={{ textAlign: "center" }}>
      <svg width={200} height={200} viewBox="0 0 100 100" style={{ display: "block", margin: "0 auto" }}>
        {renderSegments()}
      </svg>
      <table style={{ margin: "0 auto", borderCollapse: "collapse" }}>
        <thead>
          <tr>
            <th style={{ border: "1px solid black", padding: "5px" }}>Сегмент</th>
            <th style={{ border: "1px solid black", padding: "5px" }}>%</th>
          </tr>
        </thead>
        <tbody>
          {segments.map((segment, index) => (
            <tr key={index}>
              <td style={{ border: "1px solid black", padding: "5px" }}>{segment.name}</td>
              <td style={{ border: "1px solid black", padding: "5px" }}>
                {segment.percentage.toFixed(2)}%
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default RingChart;
